  if (!list.length) {
    return {
      root: [],
      rootId,
    };
  }
  const map = new Map();
  const root: any = [];
  const buttons: any = {};
  const newList = list.filter((item: any) => {
    // //根节点不确定的
    // if (isNil(rootId) || item.parentId < rootId) {
    //   rootId = item?.parentId;
    // }

    if (item?.type === 'button') {
      if (Array.isArray(buttons[item?.parentId])) {
        buttons[item.parentId].push({ label: item?.name, value: item?.id,orderIndex:item?.orderIndex || 0 });
        buttonByPid[item?.parentId].push(item?.id);
      } else {
        buttons[item.parentId] = [{ label: item?.name, value: item?.id,orderIndex:item?.orderIndex || 0 }];
        buttonByPid[item?.parentId] = [item?.id];
      }
      return false;
    }
    allMap[item?.id] = item;
    return true;
  });

  newList.forEach((item: any) => {
    const id = item?.id?.toString?.();
    const parentId = item?.parentId?.toString?.();
    const path = map.get(parentId)?.path || rootId?.toString?.();
    const newItem = {
      ...item,
      key: `${path}|${id}`,
      action: buttons?.[id],
      path: `${path}|${id}`,
    };

    menuMap[id] = `${path}|${id}`;
    if (Array.isArray(menuByPid[parentId])) {
      menuByPid[parentId].push(`${path}|${id}`);
    } else {
      menuByPid[parentId] = [`${path}|${id}`];
    }
    buttons?.[id]?.map?.((button: any) => {
      buttonMap[button?.value] = `${path}|${id}`;
    });

    map.set(id, newItem);

    if (parentId === rootId?.toString?.()) {
      root.push(map.get(id));
    } else {
      const parent = map.get(parentId);
      if (parent?.children?.length) {
        parent.children.push(map.get(id));
      } else if (parent) {
        parent.children = [map.get(id)];
      }
    }
  });

  return {
    root,
    rootId,
  };
};

export function getId(key: string) {
  const ids = key.split('|');
  if (ids?.length) {
    return ids[ids.length - 1] || '';
  }
  return '';
}

export function getParentPath(path: string) {
  const numbers = path.split('|');

  let result = [];

  let prefix = '';

  for (let i = 0; i < numbers.length; i++) {
    prefix += numbers[i];
