        buttonByPid[item?.parentId] = [item?.id];
      }
      return false;
    }
    allMap[item?.id] = item;
    return true;
  });

  // 先将所有节点加入 map（不建立父子关系），确保后续查找父节点时能找到
  newList.forEach((item: any) => {
    const id = item?.id?.toString?.();
    map.set(id, { ...item, id });
  });

  // 递归获取节点的完整路径，确保父节点路径先被计算
  const getPath = (id: string): string => {
    const item = map.get(id);
    if (!item) return rootId?.toString?.();
    if (item.path) return item.path; // 已经计算过

    const parentId = item.parentId?.toString?.();
    const parentPath = parentId === rootId?.toString?.() ? rootId?.toString?.() : getPath(parentId);
    item.path = `${parentPath}|${id}`;
    return item.path;
  };

  // 第二遍：建立父子关系和路径
  newList.forEach((item: any) => {
    const id = item?.id?.toString?.();
    const parentId = item?.parentId?.toString?.();
    const path = getPath(id);

    const newItem = {
      ...map.get(id),
      key: path,
      action: buttons?.[id],
      path,
    };

    menuMap[id] = path;
    if (Array.isArray(menuByPid[parentId])) {
      menuByPid[parentId].push(path);
    } else {
      menuByPid[parentId] = [path];
    }
    buttons?.[id]?.map?.((button: any) => {
      buttonMap[button?.value] = path;
    });

    map.set(id, newItem);

    if (parentId === rootId?.toString?.()) {
      root.push(newItem);
    } else {
      const parent = map.get(parentId);
      if (parent) {
        if (parent?.children?.length) {
          parent.children.push(newItem);
        } else {
          parent.children = [newItem];
        }
      }
    }
  });

  return {
    root,
    rootId,
  };
};

export function getId(key: string) {
  const ids = key.split('|');
  if (ids?.length) {
    return ids[ids.length - 1] || '';
  }
  return '';
}

export function getParentPath(path: string) {
  const numbers = path.split('|');

  let result = [];

  let prefix = '';

  for (let i = 0; i < numbers.length; i++) {
    prefix += numbers[i];
    if (i < numbers.length - 1) {
      prefix += '|';
    }

    result.push(prefix);
  }

  result = result.map((item) => {
    if (item !== '' && item.endsWith('|')) {
      return item.slice(0, -1);
    }
    return item;
  });

  return result;
}

export const toArray = (selected: any) => {
  const arr = Object.keys(selected).reduce((pre: any, current: any) => {
    if (current.includes('|')) {
      pre = pre.concat(current.split('|'));
    } else {
      pre.push(current);
    }
    if (selected?.[current]) {
      pre = pre.concat(selected?.[current]);
    }
    return pre;
  }, []);

  return Array.from(new Set(arr)).sort();
};
