    const sorted = [...tasks].sort((a, b) => compareTaskIds(a.id, b.id));

    const summaries = sorted.map((task) => ({
      id: task.id,
      subject: task.subject,
      status: task.status,
      owner: task.owner || '',
      blockedBy: extractOpenDependencies(tasks, task.blockedBy),
    }));

    return this.result({
      success: true,
      metadata: {
