const MAX_CONCURRENT_BACKGROUND_TASKS = 10;
const backgroundTaskQueue: (() => Promise<void>)[] = [];
let activeBackgroundTasks = 0;

async function scheduleBackgroundTask(task: () => Promise<void>) {
    if (activeBackgroundTasks >= MAX_CONCURRENT_BACKGROUND_TASKS) {
        await new Promise(resolve => backgroundTaskQueue.push(resolve));
    }

    activeBackgroundTasks++;
    try {
        await task();
    } finally {
        activeBackgroundTasks--;
        const next = backgroundTaskQueue.shift();
        if (next) next();
    }
}
