private async fetchWithTimeout(
    url: string,
    options: RequestInit,
    timeout: number
): Promise<Response> {
    const upstreamSignal = options.signal;
    const timeoutSignal = AbortSignal.timeout(timeout);
    const combinedSignal = upstreamSignal
        ? AbortSignal.any([upstreamSignal, timeoutSignal])
