    }

    get retryCount(): number {
        return this._retryCount;
    }

    get compensationRetryCount(): number {
        return this._compensationRetryCount;
    }

    get taskStartTime(): number {
        return this._taskStartTime;
    }

    get nextRetryDelayMs(): number {
