    /**
     * æ£€æŸ¥æ˜¯å¦è¶…è¿‡æœ€å¤§è¡¥å¿é‡è¯•æ¬¡æ•°
     */
    isCompensationRetryExceeded(): boolean {
        return this._compensationRetryCount >= this.config.maxCompensationRetries;
