    /**
     * æ£€æŸ¥æ˜¯å¦å·²è¢«ä¸­æ­¢
     */
    isAborted(): boolean {
        return this._status === AgentStatus.ABORTED;
    }

    /**
     * æ£€æŸ¥æ˜¯å¦éœ€è¦é‡è¯•
     */
    needsRetry(): boolean {
